INSERT INTO `how_delivery` (`How_ID`, `Name_Th`, `Name_En`, `Description_Th`, `Description_En`) VALUES
(1, 'ไปรษณีย์ไทย (ลงทะเบียนในประเทศ)', 'Domestic registered', '<p>\r\n	<span style="color:#808080;"><em><span style="font-size:14px;"><span style="font-family: Tahoma, Arial, Helvetica, sans-serif; line-height: 20px;">บริการจัดส่งแบบปกติในราคาประหยัด ด้วยมาตรฐานการนำจ่ายแน่นอน&nbsp;</span>สามารถตรวจสอบสถานะพัสดุได้ แนะนำสำหรับลูกค้าในกรุงเทพและปริมลฑลเนื่องจากระยะเวลาในการจัดส่งไม่แตกต่างกับการส่งแบบไปรษณีย์ด่วนพิเศษ (EMS) มากนัก</span></em></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p>\r\n	<span style="color:#808080;"><em><span style="font-family: Tahoma, Arial, Helvetica, sans-serif; font-size: 12.727272033691406px; line-height: 20px;">Economical and reliable domestic transport service for packages with qualifying delivery standard.</span><span style="font-size:14px;">&nbsp;Items can be accepted and picked up at a picked&nbsp;up at any Post Office. Parcel can be 24/7 track through&nbsp;</span></em></span><em style="color: rgb(128, 128, 128); font-size: 12px;"><span style="font-size:14px;"><u>Thailand Post Track&amp;Trace Service.</u></span></em></p>\r\n<p>\r\n	&nbsp;</p>\r\n'),
(2, 'ไปรษณีย์ไทย ด่วนพิเศษ (EMS)', 'Domestic EMS', '<p>\r\n	<span style="color:#808080;"><em>การส่งแบบนี้ทางไปรษณีย์ไทยจะดำเนินการอย่างเร่งด่วน สามารถตรวจสอบสถาณะได้ผ่านทางอินเทอร์เน็ต ภายในกรุงเทพและปริมณฑลใช้เวลา 24 ชั่วโมง ต่างจังหวัด 48 ชั่วโมง</em></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '<p style="text-align: justify; ">\r\n	<em><span style="color:#808080;"><span style="font-size:14px;">Customers can send urgent mail with confidence and check its status at all times via&nbsp;<u>Thailand Post Track&amp;Trace Service.</u></span></span></em></p>\r\n<p style="text-align: justify; ">\r\n	&nbsp;</p>\r\n<p style="text-align: justify; ">\r\n	&nbsp;</p>\r\n');
